-- ----------------------------
-- Table structure for `pm_questions`
-- ----------------------------
DROP TABLE IF EXISTS `pm_questions`;
CREATE TABLE `pm_questions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `question_id` int(11) NOT NULL,
  `examine_id` int(11) NOT NULL,
  `question_title` text NOT NULL,
  `analyze` text NOT NULL,
  `answer` char(8) NOT NULL,
  `item_a` text NOT NULL,
  `item_b` text NOT NULL,
  `item_c` text NOT NULL,
  `item_d` text NOT NULL,
  PRIMARY KEY (`id`)
